pragma solidity 0.8.25;

import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";

/**
 * @title PrimeStorageV1
 * @author Venus
 * @notice Storage for Prime Token
 */
contract PrimeStorageV1 {
    struct Token {
        bool exists;
        bool isIrrevocable;
    }

    struct Market {
        uint256 supplyMultiplier;
        uint256 borrowMultiplier;
        uint256 rewardIndex;
        uint256 sumOfMembersScore;
        bool exists;
    }

    struct Interest {
        uint256 accrued;
        uint256 score;
        uint256 rewardIndex;
    }

    struct PendingReward {
        address vToken;
        address rewardToken;
        uint256 amount;
    }

    /// @notice Base unit for computations, usually used in scaling (multiplications, divisions)
    uint256 internal constant EXP_SCALE = 1e18;

    /// @notice maximum BPS = 100%
    uint256 internal constant MAXIMUM_BPS = 1e4;

    /// @notice Mapping to get prime token's metadata
    mapping(address => Token) public tokens;

    /// @notice  Tracks total irrevocable tokens minted
    uint256 public totalIrrevocable;

    /// @notice  Tracks total revocable tokens minted
    uint256 public totalRevocable;

    /// @notice  Indicates maximum revocable tokens that can be minted
    uint256 public revocableLimit;

    /// @notice  Indicates maximum irrevocable tokens that can be minted
    uint256 public irrevocableLimit;

    /// @notice Tracks when prime token eligible users started staking for claiming prime token
    mapping(address => uint256) public stakedAt;

    /// @notice vToken to market configuration
    mapping(address => Market) public markets;

    /// @notice vToken to user to user index
    mapping(address => mapping(address => Interest)) public interests;

    /// @notice A list of boosted markets
    address[] internal _allMarkets;

    /// @notice numerator of alpha. Ex: if alpha is 0.5 then this will be 1
    uint128 public alphaNumerator;

    /// @notice denominator of alpha. Ex: if alpha is 0.5 then this will be 2
    uint128 public alphaDenominator;

    /// @notice address of XVS vault
    address public xvsVault;

    /// @notice address of XVS vault reward token
    address public xvsVaultRewardToken;

    /// @notice address of XVS vault pool id
    uint256 public xvsVaultPoolId;

    /// @notice mapping to check if a account's score was updated in the round
    mapping(uint256 => mapping(address => bool)) public isScoreUpdated;

    /// @notice unique id for next round
    uint256 public nextScoreUpdateRoundId;

    /// @notice total number of accounts whose score needs to be updated
    uint256 public totalScoreUpdatesRequired;

    /// @notice total number of accounts whose score is yet to be updated
    uint256 public pendingScoreUpdates;

    /// @notice mapping used to find if an asset is part of prime markets
    mapping(address => address) public vTokenForAsset;

    /// @notice Address of core pool comptroller contract
    address internal corePoolComptroller;

    /// @notice unreleased income from PLP that's already distributed to prime holders
    /// @dev mapping of asset address => amount
    mapping(address => uint256) public unreleasedPLPIncome;

    /// @notice The address of PLP contract
    address public primeLiquidityProvider;

    /// @notice The address of ResilientOracle contract
    ResilientOracleInterface public oracle;

    /// @notice The address of PoolRegistry contract
    address public poolRegistry;

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    uint256[26] private __gap;
}
","keccak256":"0x5285c875114db2ea2be0b81b65722d5761806217022db733323c4e03365a95e7","license":"BSD-3-Clause"},"contracts/flash-swap/ExactOutputFlashSwap.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import { IPancakeV3SwapCallback } from "@pancakeswap/v3-core/contracts/interfaces/callback/IPancakeV3SwapCallback.sol";
import { IPancakeV3Pool } from "@pancakeswap/v3-core/contracts/interfaces/IPancakeV3Pool.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { approveOrRevert } from "../util/approveOrRevert.sol";
import { ISmartRouter } from "../third-party/pancakeswap-v8/ISmartRouter.sol";
import { Path } from "../third-party/pancakeswap-v8/Path.sol";
import { PoolAddress } from "../third-party/pancakeswap-v8/PoolAddress.sol";
import { MIN_SQRT_RATIO, MAX_SQRT_RATIO } from "../third-party/pancakeswap-v8/constants.sol";

import { FlashHandler } from "./FlashHandler.sol";

/// @notice Callback data passed to the swap callback
struct Envelope {
    /// @notice Exact-output (reversed) swap path, starting with tokenY and ending with tokenX
    bytes path;
    /// @notice Application-specific data
    bytes data;
    /// @notice Pool key of the pool that should have called the callback
    PoolAddress.PoolKey poolKey;
}

/// @title ExactOutputFlashSwap
/// @notice A base contract for exact output flash swap operations.
///
///   Upon calling _flashSwap, swaps tokenX to tokenY using a flash swap, i.e. the contract:
///
///   1. Invokes the flash swap on the first pool from the path
///   2. Receives tokenY from the pool
///   3. Calls _onMoneyReceived, which should ensure that the contract has enough tokenX
///      to repay the flash swap
///   4. Repays the flash swap with tokenX (doing the conversion if necessary)
///   5. Calls _onFlashCompleted
///
/// @dev This contract is abstract and should be inherited by a contract that implements
///   _onMoneyReceived and _onFlashCompleted. Note that in the callbacks transaction
///   context (sender and value) is different from the original context. The inheriting
///   contracts should save the original context in the application-specific data bytes
///   passed to the callbacks.
abstract contract ExactOutputFlashSwap is IPancakeV3SwapCallback, FlashHandler {
    using SafeERC20 for IERC20;
    using Path for bytes;

    /// @notice Thrown if the swap callback is called with unexpected or zero amount of tokens
    error EmptySwap();

    /// @notice Callback called by PancakeSwap pool during flash swap conversion
    /// @param amount0Delta Amount of pool's token0 to repay for the flash swap (negative if no need to repay this token)
    /// @param amount1Delta Amount of pool's token1 to repay for the flash swap (negative if no need to repay this token)
    /// @param data Callback data containing an Envelope structure
    function pancakeV3SwapCallback(int256 amount0Delta, int256 amount1Delta, bytes calldata data) external {
        Envelope memory envelope = abi.decode(data, (Envelope));
        _verifyCallback(envelope.poolKey);
        if (amount0Delta <= 0 && amount1Delta <= 0) {
            revert EmptySwap();
        }

        uint256 amountToPay;
        IERC20 tokenToPay;
        if (amount0Delta > 0) {
            tokenToPay = IERC20(envelope.poolKey.token0);
            amountToPay = uint256(amount0Delta);
        } else if (amount1Delta > 0) {
            tokenToPay = IERC20(envelope.poolKey.token1);
            amountToPay = uint256(amount1Delta);
        }

        (IERC20 tokenIn, uint256 maxAmountIn) = _onMoneyReceived(envelope.data);

        if (envelope.path.hasMultiplePools()) {
            bytes memory remainingPath = envelope.path.skipToken();
            approveOrRevert(tokenIn, address(SWAP_ROUTER), maxAmountIn);
            SWAP_ROUTER.exactOutput(
                ISmartRouter.ExactOutputParams({
                    path: remainingPath,
                    recipient: msg.sender, // repaying to the pool
                    amountOut: amountToPay,
                    amountInMaximum: maxAmountIn
                })
            );
            approveOrRevert(tokenIn, address(SWAP_ROUTER), 0);
        } else {
            // If the path had just one pool, tokenToPay should be tokenX, so we can just repay the debt.
            tokenToPay.safeTransfer(msg.sender, amountToPay);
        }

        _onFlashCompleted(envelope.data);
    }

    /// @dev Initiates a flash swap
    /// @param amountOut Amount of tokenY to receive during the flash swap
    /// @param path Exact-output (reversed) swap path, starting with tokenY and ending with tokenX
    /// @param data Application-specific data
    function _flashSwap(uint256 amountOut, bytes calldata path, bytes memory data) internal {
        (address tokenY, address tokenB, uint24 fee) = path.decodeFirstPool();
        PoolAddress.PoolKey memory poolKey = PoolAddress.getPoolKey(tokenY, tokenB, fee);
        IPancakeV3Pool pool = IPancakeV3Pool(PoolAddress.computeAddress(DEPLOYER, poolKey));
        bytes memory envelope = abi.encode(Envelope(path, data, poolKey));

        bool swapZeroForOne = poolKey.token1 == tokenY;
        uint160 sqrtPriceLimitX96 = (swapZeroForOne ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1);
        pool.swap(address(this), swapZeroForOne, -int256(amountOut), sqrtPriceLimitX96, envelope);
    }
}
","keccak256":"0xedd6f5eef6910cff4b3d37866059f08093dba242d67618b68fe9deaf27dc6294","license":"MIT"},"contracts/flash-swap/FlashHandler.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";

import { ISmartRouter } from "../third-party/pancakeswap-v8/ISmartRouter.sol";
import { PoolAddress } from "../third-party/pancakeswap-v8/PoolAddress.sol";

abstract contract FlashHandler {
    /// @notice The PancakeSwap SmartRouter contract
    ISmartRouter public immutable SWAP_ROUTER;

    /// @notice The PancakeSwap deployer contract
    address public immutable DEPLOYER;

    /// @notice Thrown if operation callback is called by a non-PancakeSwap contract
    /// @param expected Expected callback sender (pool address computed based on the pool key)
    /// @param actual Actual callback sender
    error InvalidCallbackSender(address expected, address actual);

    /// @param swapRouter_ PancakeSwap SmartRouter contract
    constructor(ISmartRouter swapRouter_) {
        ensureNonzeroAddress(address(swapRouter_));

        SWAP_ROUTER = swapRouter_;
        DEPLOYER = swapRouter_.deployer();
    }

    /// @dev Called when token Y is received during a flash operation. This function has to ensure
    ///   that at the end of the execution the contract has enough token X to repay the flash
    ///   operation.
    ///   Note that msg.sender is the pool that called the callback, not the original caller
    ///   of the transaction where flash operation was invoked.
    /// @param data Application-specific data
    /// @return tokenIn Token X
    /// @return maxAmountIn Maximum amount of token X to be used to repay the flash operation
    function _onMoneyReceived(bytes memory data) internal virtual returns (IERC20 tokenIn, uint256 maxAmountIn);

    /// @dev Called when the flash operation is completed and was paid for. By default, does nothing.
    ///   Note that msg.sender is the pool that called the callback, not the original caller
    ///   of the transaction where flash operation was invoked.
    /// @param data Application-specific data
    // solhint-disable-next-line no-empty-blocks
    function _onFlashCompleted(bytes memory data) internal virtual {}

    /// @dev Ensures that the caller of a callback is a legitimate PancakeSwap pool
    /// @param poolKey The pool key of the pool to verify
    function _verifyCallback(PoolAddress.PoolKey memory poolKey) internal view {
        address pool = PoolAddress.computeAddress(DEPLOYER, poolKey);
        if (msg.sender != pool) {
            revert InvalidCallbackSender(pool, msg.sender);
        }
    }
}
","keccak256":"0x957578eb6a07d56e316b048bddfcff757ea3d2136040239a02053c63588467ec","license":"MIT"},"contracts/flash-swap/FlashLoan.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import { IPancakeV3FlashCallback } from "@pancakeswap/v3-core/contracts/interfaces/callback/IPancakeV3FlashCallback.sol";
import { IPancakeV3Pool } from "@pancakeswap/v3-core/contracts/interfaces/IPancakeV3Pool.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { Path } from "../third-party/pancakeswap-v8/Path.sol";
import { PoolAddress } from "../third-party/pancakeswap-v8/PoolAddress.sol";

import { FlashHandler } from "./FlashHandler.sol";

/// @notice Callback data passed to the flash loan callback
struct Envelope {
    /// @notice Token (the same as path[0])
    IERC20 token;
    /// @notice Amount of token to receive during the flash loan
    uint256 amountOut;
    /// @notice Application-specific data
    bytes data;
    /// @notice Pool key of the pool that should have called the callback
    PoolAddress.PoolKey poolKey;
}

/// @title FlashLoan
/// @notice A base contract for flash loan operations.
///
///   Upon calling _flashLoan, flash-loans path[0] token in the specified pool:
///
///   1. Invokes the flash loan on the first pool in the path
///   2. Receives the specified amount of token from the pool
///   3. Calls _onMoneyReceived, which should ensure that the contract has enough tokens
///      to repay the flash swap
///   4. Repays the flash loan with the tokens
///   5. Calls _onFlashCompleted
///
/// @dev This contract is abstract and should be inherited by a contract that implements
///   _onMoneyReceived and _onFlashCompleted. Note that in the callbacks transaction
///   context (sender and value) is different from the original context. The inheriting
///   contracts should save the original context in the application-specific data bytes
///   passed to the callbacks.
abstract contract FlashLoan is IPancakeV3FlashCallback, FlashHandler {
    using SafeERC20 for IERC20;
    using Path for bytes;

    /// @notice Flash swap parameters
    struct FlashLoanParams {
        uint256 amountOut;
        /// @notice Exact-output (reversed) swap path, starting with tokenY and ending with tokenX
        bytes path;
        /// @notice Application-specific data
        bytes data;
    }

    /// @notice Thrown if the application tries a flash loan with token to repay != borrowed token
    error UnexpectedFlashLoan(address tokenX, address tokenY);

    /// @notice Thrown if maxAmountIn threshold is violated while repaying a flash loan
    error MaxAmountInViolated(uint256 amountToPay, uint256 maxAmountIn);

    /// @notice Callback called by PancakeSwap pool during a flash loan. Does a sanity check
    ///  that token to repay is equal to the borrowed token, and repays the flash loan, capped
    ///  to the specified maxAmountIn. Invokes _onFlashCompleted once the flash loan is repaid.
    /// @param fee0 Fee amount in pool's token0
    /// @param fee1 Fee amount in pool's token1
    /// @param data Callback data containing an Envelope structure
    function pancakeV3FlashCallback(uint256 fee0, uint256 fee1, bytes memory data) external {
        Envelope memory envelope = abi.decode(data, (Envelope));
        _verifyCallback(envelope.poolKey);

        (IERC20 tokenIn, uint256 maxAmountIn) = _onMoneyReceived(envelope.data);

        if (tokenIn != envelope.token) {
            revert UnexpectedFlashLoan(address(tokenIn), address(envelope.token));
        }

        uint256 fee = (fee0 == 0 ? fee1 : fee0);
        uint256 amountToPay = envelope.amountOut + fee;
        if (amountToPay > maxAmountIn) {
            revert MaxAmountInViolated(amountToPay, maxAmountIn);
        }

        envelope.token.safeTransfer(msg.sender, envelope.amountOut + fee);

        _onFlashCompleted(envelope.data);
    }

    /// @dev Initiates a flash loan using a path passed in the calldata
    /// @param amountOut Amount of path[0] token to receive during the flash loan
    /// @param path A path containing a single pool to flash-loan from
    /// @param data Application-specific data to pass to the callbacks
    function _flashLoan(uint256 amountOut, bytes calldata path, bytes memory data) internal {
        (address tokenA, address tokenB, uint24 fee) = path.decodeFirstPool();
        PoolAddress.PoolKey memory poolKey = PoolAddress.getPoolKey(tokenA, tokenB, fee);
        _flashLoan(amountOut, tokenA, poolKey, data);
    }

    /// @dev Initiates a flash loan using a token address and a pool key
    /// @param amountOut Amount of token to receive during the flash loan
    /// @param token Token to flash-borrow
    /// @param poolKey A pool key of the pool to flash-loan from
    /// @param data Application-specific data to pass to the callbacks
    function _flashLoan(
        uint256 amountOut,
        address token,
        PoolAddress.PoolKey memory poolKey,
        bytes memory data
    ) internal {
        IPancakeV3Pool pool = IPancakeV3Pool(PoolAddress.computeAddress(DEPLOYER, poolKey));
        pool.flash(
            address(this),
            poolKey.token0 == token ? amountOut : 0,
            poolKey.token1 == token ? amountOut : 0,
            abi.encode(Envelope(IERC20(token), amountOut, data, poolKey))
        );
    }
}
","keccak256":"0x39b099151e65d6d52768a47e06855380fe2c4853bbd45c86bd009f3066ac6b04","license":"MIT"},"contracts/operators/LiquidationOperator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { VTokenInterface } from "@venusprotocol/isolated-pools/contracts/VTokenInterfaces.sol";

import { FlashHandler } from "../flash-swap/FlashHandler.sol";
import { ExactOutputFlashSwap } from "../flash-swap/ExactOutputFlashSwap.sol";
import { FlashLoan } from "../flash-swap/FlashLoan.sol";
import { ISmartRouter } from "../third-party/pancakeswap-v8/ISmartRouter.sol";
import { approveOrRevert } from "../util/approveOrRevert.sol";
import { transferAll } from "../util/transferAll.sol";
import { checkDeadline, validatePathStart, validatePathEnd } from "../util/validators.sol";

contract LiquidationOperator is ExactOutputFlashSwap, FlashLoan {
    using SafeERC20 for IERC20;

    /// @notice Liquidation parameters
    struct FlashLiquidationParameters {
        /// @notice The receiver of the liquidated collateral
        address beneficiary;
        /// @notice vToken for the borrowed underlying
        VTokenInterface vTokenBorrowed;
        /// @notice Borrower whose position is being liquidated
        address borrower;
        /// @notice Amount of borrowed tokens to repay
        uint256 repayAmount;
        /// @notice Collateral vToken to seize
        VTokenInterface vTokenCollateral;
        /// @notice Reversed (!) swap path to use for liquidation. For regular (not in-kind)
        /// liquidations it should start with the borrowed token and end with the collateral
        /// token. For in-kind liquidations, must consist of a single PancakeSwap pool to
        /// source the liquidity from.
        bytes path;
    }

    /// @notice Liquidation data to pass between the calls
    struct FlashLiquidationData {
        /// @notice The receiver of the liquidated collateral
        address beneficiary;
        /// @notice The borrowed underlying
        IERC20 borrowedUnderlying;
        /// @notice vToken for the borrowed underlying
        VTokenInterface vTokenBorrowed;
        /// @notice Borrower whose position is being liquidated
        address borrower;
        /// @notice Amount of borrowed tokens to repay
        uint256 repayAmount;
        /// @notice Collateral asset
        IERC20 collateralUnderlying;
        /// @notice Collateral vToken to seize
        VTokenInterface vTokenCollateral;
    }

    /// @notice Thrown if vToken.redeem(...) returns a nonzero error code
    error RedeemFailed(uint256 errorCode);

    /// @param swapRouter_ PancakeSwap SmartRouter contract
    constructor(ISmartRouter swapRouter_) FlashHandler(swapRouter_) {}

    /// @notice Liquidates a borrower's position using flash swap or a flash loan
    /// @param params Liquidation parameters
    function liquidate(FlashLiquidationParameters calldata params, uint256 deadline) external {
        checkDeadline(deadline);

        address borrowedTokenAddress = params.vTokenBorrowed.underlying();
        address collateralTokenAddress = params.vTokenCollateral.underlying();

        uint256 repayAmount;
        if (params.repayAmount == type(uint256).max) {
            repayAmount = params.vTokenBorrowed.borrowBalanceCurrent(params.borrower);
        } else {
            repayAmount = params.repayAmount;
        }

        validatePathStart(params.path, borrowedTokenAddress);
        FlashLiquidationData memory data = FlashLiquidationData({
            beneficiary: params.beneficiary,
            borrowedUnderlying: IERC20(borrowedTokenAddress),
            vTokenBorrowed: params.vTokenBorrowed,
            borrower: params.borrower,
            repayAmount: repayAmount,
            collateralUnderlying: IERC20(collateralTokenAddress),
            vTokenCollateral: params.vTokenCollateral
        });

        if (collateralTokenAddress == borrowedTokenAddress) {
            _flashLoan(params.repayAmount, params.path, abi.encode(data));
        } else {
            validatePathEnd(params.path, collateralTokenAddress);
            _flashSwap(params.repayAmount, params.path, abi.encode(data));
        }
    }

    function _onMoneyReceived(bytes memory data) internal override returns (IERC20 tokenIn, uint256 maxAmountIn) {
        FlashLiquidationData memory data = abi.decode(data, (FlashLiquidationData));

        approveOrRevert(data.borrowedUnderlying, address(data.vTokenBorrowed), data.repayAmount);
        data.vTokenBorrowed.liquidateBorrow(data.borrower, data.repayAmount, data.vTokenCollateral);
        approveOrRevert(data.borrowedUnderlying, address(data.vTokenBorrowed), 0);

        _redeem(data.vTokenCollateral, data.vTokenCollateral.balanceOf(address(this)));

        return (data.collateralUnderlying, data.collateralUnderlying.balanceOf(address(this)));
    }

    function _onFlashCompleted(bytes memory data) internal override {
        FlashLiquidationData memory data = abi.decode(data, (FlashLiquidationData));
        transferAll(data.collateralUnderlying, address(this), data.beneficiary);
    }

    /// @dev Redeems ERC-20 tokens from the given vToken
    /// @param vToken The vToken to redeem tokens from
    /// @param vTokenAmount The amount of vTokens to redeem
    function _redeem(VTokenInterface vToken, uint256 vTokenAmount) internal {
        uint256 errorCode = vToken.redeem(vTokenAmount);
        if (errorCode != 0) {
            revert RedeemFailed(errorCode);
        }
    }
}
","keccak256":"0x398d91d8ec242cce8c7125940d60df025a3659816257197be23caea0e1d640de","license":"MIT"},"contracts/third-party/pancakeswap-v8/BytesLib.sol":{"content":"// SPDX-License-Identifier: GPL-2.0-or-later
/*
 * @title Solidity Bytes Arrays Utils
 * @author Gonçalo Sá <goncalo.sa@consensys.net>
 *
 * @dev Bytes tightly packed arrays utility library for ethereum contracts written in Solidity.
 *      The library lets you concatenate, slice and type cast bytes arrays both in memory and storage.
 */
pragma solidity >=0.8.0 <0.9.0;

library BytesLib {
    function slice(bytes memory _bytes, uint256 _start, uint256 _length) internal pure returns (bytes memory) {
        require(_length + 31 >= _length, "slice_overflow");
        require(_bytes.length >= _start + _length, "slice_outOfBounds");

        bytes memory tempBytes;

        assembly {
            switch iszero(_length)
            case 0 {
                // Get a location of some free memory and store it in tempBytes as
                // Solidity does for memory variables.
                tempBytes := mload(0x40)

                // The first word of the slice result is potentially a partial
                // word read from the original array. To read it, we calculate
                // the length of that partial word and start copying that many
                // bytes into the array. The first word we copy will start with
                // data we don't care about, but the last `lengthmod` bytes will
                // land at the beginning of the contents of the new array. When
                // we're done copying, we overwrite the full first word with
                // the actual length of the slice.
                let lengthmod := and(_length, 31)

                // The multiplication in the next line is necessary
                // because when slicing multiples of 32 bytes (lengthmod == 0)
                // the following copy loop was copying the origin's length
                // and then ending prematurely not copying everything it should.
                let mc := add(add(tempBytes, lengthmod), mul(0x20, iszero(lengthmod)))
                let end := add(mc, _length)

                for {
                    // The multiplication in the next line has the same exact purpose
                    // as the one above.
                    let cc := add(add(add(_bytes, lengthmod), mul(0x20, iszero(lengthmod))), _start)
                } lt(mc, end) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    mstore(mc, mload(cc))
                }

                mstore(tempBytes, _length)

                //update free-memory pointer
                //allocating the array padded to 32 bytes like the compiler does now
                mstore(0x40, and(add(mc, 31), not(31)))
            }
            //if we want a zero-length slice let's just return a zero-length array
            default {
                tempBytes := mload(0x40)
                //zero out the 32 bytes slice we are about to return
                //we need to do it because Solidity does not garbage collect
                mstore(tempBytes, 0)

                mstore(0x40, add(tempBytes, 0x20))
            }
        }

        return tempBytes;
    }

    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        require(_bytes.length >= _start + 20, "toAddress_outOfBounds");
        address tempAddress;

        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }

        return tempAddress;
    }

    function toUint24(bytes memory _bytes, uint256 _start) internal pure returns (uint24) {
        require(_start + 3 >= _start, "toUint24_overflow");
        require(_bytes.length >= _start + 3, "toUint24_outOfBounds");
        uint24 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x3), _start))
        }

        return tempUint;
    }
}
